/*
** Basic Types
*/
// there are 3 basic types (boolean, number, string)
var isOK = true;
var answer = 42;
var user = "Mark";
console.log(isOK);
console.log(answer);
console.log(user);
// use 'any' when type is not known
var whatever = 42;
whatever = "something else";
whatever = false;
console.log(whatever);
